// ===== Parse reply + MQTT Discovery/States (marstek/venus_e) =====
// INPUTS:
//   - UDP Antworten vom Speicher (JSON-RPC mit festen IDs 100..105)
//   - ein kÃ¼nstliches Inject mit topic="__discovery__"
// OUTPUTS:
//   output 1 â†’ UDP Requests (normal leer; nur falls Discovery spÃ¤ter was triggern mÃ¼sste)
//   output 2 â†’ MQTT Discovery configs (retain:true)
//   output 3 â†’ MQTT State publishes (stat/* Werte)
//
// Topics/Prefixes:
//   discoBase = homeassistant
//   statBase  = marstek/venus_e/stat
//   cmndBase  = marstek/venus_e/cmnd
//
// Wir announcen hier ALLES zentral:
//   - Sensors
//   - Select fÃ¼r Betriebsmodus
//   - Switch Winterbetrieb
//   - Switch Lagerungsbetrieb
//   (separater Discovery-Node nicht mehr nÃ¶tig)

const discoBase = 'homeassistant';
const devId     = 'marstek_venus_e_v3_UDP';
const devName   = 'Marstek VenusE 3.0';
const manu      = 'Marstek';
const model     = 'VenusE 3.0';
const statBase  = 'marstek/venus_e/stat';
const cmndBase  = 'marstek/venus_e/cmnd';

function T(leaf){ return `${statBase}/${leaf}`; }
function pub(leaf, val){
    return { topic: T(leaf), payload: String(val) };
}

if (msg && msg.topic === '__discovery__') {
    const d = [];
    const deviceInfo = {
        identifiers: [devId],
        manufacturer: manu,
        model,
        name: devName
    };

    const add = (t,p) => d.push({
        topic: t,
        payload: JSON.stringify(p),
        retain: true
    });

    const S = (oid, name, leaf, unit) => add(
        `${discoBase}/sensor/${oid}/config`,
        {
            name,
            unique_id: oid,
            state_topic: `${statBase}/${leaf}`,
            unit_of_measurement: unit,
            device: deviceInfo
        }
    );

    const SEL = (oid, name, opts, cmdLeaf, stateLeaf) => add(
        `${discoBase}/select/${oid}/config`,
        {
            name,
            unique_id: oid,
            command_topic: `${cmndBase}/${cmdLeaf}`,
            state_topic: `${statBase}/${stateLeaf}`,
            options: opts,
            device: deviceInfo
        }
    );

    // ---------- Sensoren ----------
    S('marstek_venus_device',         'GerÃ¤t/Modell',            'device');
    S('marstek_venus_fw_ver',         'Firmware Version',        'fw_ver');
    S('marstek_venus_ble_mac',        'BLE MAC',                 'ble_mac');
    S('marstek_venus_wifi_mac',       'WLAN MAC',                'wifi_mac');
    S('marstek_venus_wifi_ssid',      'WLAN SSID',               'wifi_ssid');
    S('marstek_venus_wifi_rssi',      'WLAN RSSI',               'wifi_rssi', 'dBm');
    S('marstek_venus_ble_state',      'BLE Status',              'ble_state');
    S('marstek_venus_bat_soc',        'Batterie SoC',            'bat_soc', '%');
    S('marstek_venus_bat_temp_c',     'Batterie Temperatur',     'bat_temp_c', 'Â°C');
    S('marstek_venus_bat_capacity',   'Verbleibende KapazitÃ¤t',  'bat_capacity', 'Wh');
    S('marstek_venus_rated_capacity', 'NennkapazitÃ¤t',           'rated_capacity', 'Wh');
    S('marstek_venus_charg_flag',     'Charging erlaubt',        'charg_flag');
    S('marstek_venus_dischrg_flag',   'Discharge erlaubt',       'dischrg_flag');
    S('marstek_venus_mode',           'Modus',                   'mode');
    S('marstek_venus_ongrid_power',   'Netzleistung',            'ongrid_power', 'W');
    S('marstek_venus_offgrid_power',  'Offgrid-Leistung',        'offgrid_power', 'W');
    S('marstek_venus_es_bat_soc',     'Batterie SoC (ES)',       'es_bat_soc', '%');
    S('marstek_venus_total_power',    'Gesamtleistung',          'total_power', 'W');
    S('marstek_venus_em_ct_state',    'CT Status (EM)',          'em_ct_state');
    S('marstek_venus_em_a_power',     'EM Phase A',              'em_a_power', 'W');
    S('marstek_venus_em_b_power',     'EM Phase B',              'em_b_power', 'W');
    S('marstek_venus_em_c_power',     'EM Phase C',              'em_c_power', 'W');
    S('marstek_venus_em_total_power', 'EM Gesamtleistung',       'em_total_power', 'W');

    // ---------- Select (Betriebsmodus) ----------
    SEL(
        'marstek_venus_mode_select',
        'Betriebsmodus',
        ['Auto','AI','Manual','Passive'],
        'mode',
        'mode'
    );

    // ---------- Winterbetrieb-Schalter ----------
    add(
        `${discoBase}/switch/marstek_venus_winter_mode_switch/config`,
        {
            name: 'Winterbetrieb',
            unique_id: 'marstek_venus_winter_mode_switch',
            command_topic: 'marstek/winter/cmnd/winter_mode',
            payload_on: 'ON',
            payload_off: 'OFF',
            state_topic: 'marstek/winter/stat/winter_mode',
            state_on: 'ON',
            state_off: 'OFF',
            icon: 'mdi:snowflake',
            device: deviceInfo
        }
    );

    // ---------- Lagerungsbetrieb-Schalter ----------
    add(
        `${discoBase}/switch/marstek_venus_storage_switch/config`,
        {
            name: 'Lagerungsbetrieb',
            unique_id: 'marstek_venus_storage_switch',
            command_topic: 'marstek/storage/cmnd/enable',
            payload_on: 'ON',
            payload_off: 'OFF',
            state_topic: 'marstek/storage/stat/enable',
            state_on: 'ON',
            state_off: 'OFF',
            icon: 'mdi:battery-clock',
            device: deviceInfo
        }
    );

    return [ null, d, null ];
}

