let txt;
try {
    txt = msg.payload.toString('utf8');
} catch(e) {
    return null;
}
let js;
try {
    js = JSON.parse(txt);
} catch(e) {
    node.warn('JSON parse error: ' + e);
    return null;
}
if (!js || (!js.result && !js.error)) {
    return null;
}

const out = [];
if (js.result) {
    if (js.id === 100) {
        if (js.result.device      !== undefined) out.push(pub('device',        js.result.device));
        if (js.result.ver         !== undefined) out.push(pub('fw_ver',        js.result.ver));
        if (js.result.ble_mac     !== undefined) out.push(pub('ble_mac',       js.result.ble_mac));
        if (js.result.wifi_mac    !== undefined) out.push(pub('wifi_mac',      js.result.wifi_mac));
        if (js.result.wifi_name   !== undefined) out.push(pub('wifi_ssid',     js.result.wifi_name));
    }
    if (js.id === 101) {
        if (js.result.ssid        !== undefined) out.push(pub('wifi_ssid',     js.result.ssid));
        if (js.result.rssi        !== undefined) out.push(pub('wifi_rssi',     js.result.rssi));
    }
    if (js.id === 102) {
        if (js.result.state       !== undefined) out.push(pub('ble_state',     js.result.state));
        if (js.result.ble_mac     !== undefined) out.push(pub('ble_mac',       js.result.ble_mac));
    }
    if (js.id === 103) {
        const soc = js.result.soc;
        const tr  = js.result.bat_temp;
        let tc;
        if (typeof tr === 'number') {
            tc = Math.round((tr / 10) * 10) / 10; // Zehntel-Â°C zu Â°C mit 1 NK
        }
        if (soc                          !== undefined) out.push(pub('bat_soc',        soc));
        if (tc                           !== undefined) out.push(pub('bat_temp_c',     tc));
        if (js.result.bat_capacity       !== undefined) out.push(pub('bat_capacity',   js.result.bat_capacity));
        if (js.result.rated_capacity     !== undefined) out.push(pub('rated_capacity', js.result.rated_capacity));
        if (js.result.charg_flag         !== undefined) out.push(pub('charg_flag',     js.result.charg_flag));
        if (js.result.dischrg_flag       !== undefined) out.push(pub('dischrg_flag',   js.result.dischrg_flag));
    }
    if (js.id === 104) {
        if (js.result.mode              !== undefined) out.push(pub('mode',            js.result.mode));
        if (js.result.ongrid_power      !== undefined) out.push(pub('ongrid_power',    js.result.ongrid_power));
        if (js.result.offgrid_power     !== undefined) out.push(pub('offgrid_power',   js.result.offgrid_power));
        if (js.result.bat_soc           !== undefined) out.push(pub('es_bat_soc',      js.result.bat_soc));
        if (js.result.total_power       !== undefined) out.push(pub('total_power',     js.result.total_power));
    }
    if (js.id === 105) {
        if (js.result.ct_state    !== undefined) out.push(pub('em_ct_state',   js.result.ct_state));
        if (js.result.a_power     !== undefined) out.push(pub('em_a_power',    js.result.a_power));
        if (js.result.b_power     !== undefined) out.push(pub('em_b_power',    js.result.b_power));
        if (js.result.c_power     !== undefined) out.push(pub('em_c_power',    js.result.c_power));
        if (js.result.total_power !== undefined) out.push(pub('em_total_power',js.result.total_power));
    }
}
return [ null, null, out ];

const v = String(msg.payload||'').trim().toUpperCase();
const on = (v==='ON'||v==='1'||v==='TRUE');
