// ===== WINTER Controller v2.3 (bereinigt, schreibt in marstek.log) =====
// Output 1: MQTT  (Status / Events / History / und Mode-Befehle an marstek/venus_e/cmnd/mode)
// Output 2: File  (Logzeile pro Ereignis)
//
// Logik Winterbetrieb (wenn aktiv UND keine Lagerung lÃ¤uft):
// - Bei PV-Ãœberschuss stabil genug  -> WINTER â†’ AUTO  (Akku darf laden selbstÃ¤ndig)
// - Bei Netzbezug & niedrigem SoC   -> WINTER â†’ PASSIVE (Akku soll nicht ins Haus entladen)
//
// NEU: Wenn Lagerungsbetrieb aktiv ist (storage_active=true), dann
// - macht dieser Node GAR NICHTS
// - kein AUTO, kein PASSIVE, keine Timer
// - Statusanzeige wird blau mit Hinweis "[PAUSE wg. Lagerung]"
//
// ZustÃ¤nde im flow-Kontext:
//   winter_enabled  (bool)
//   storage_active  (bool)
//   soc             (Number, %)
//   surplus         (Number, W)
//   vgrid           (Number, W)
//   tStart, tStop, mute_until
//   winter_log      (Array<String>)

const LOG_TOPIC = 'marstek/winter/stat/log';
const LOG_HISTORY_TOPIC = LOG_TOPIC + '/history';

const SURPLUS_START_W   = -200;
const SURPLUS_FOR_MS    = 30000;
const VGRID_STOP_W      = 100;
const STOP_FOR_MS       = 30000;
const STOP_FAST_MS      = 5000;
const MIN_SOC           = 50;
const FAST_SOC_LIMIT    = 41;
const PASSIVE_COOLDOWN_MS = 60000;

function pushLog(mqttArr, fileArr, logsArr, line) {
    const entry = `[${new Date().toISOString()}] ${line}`;
    logsArr.push(entry);
    mqttArr.push({ topic: LOG_TOPIC, payload: entry });
    fileArr.push({ payload: entry });
}

const enabled       = flow.get('winter_enabled') || false;
const storageActive = flow.get('storage_active') || false;

let soc       = flow.get('soc');
let surplus   = flow.get('surplus');
let vgrid     = flow.get('vgrid');
let tStart    = flow.get('tStart');
let tStop     = flow.get('tStop');
let muteUntil = flow.get('mute_until') || 0;

if (msg.src === 'soc')        { soc     = Number(msg.val); flow.set('soc', soc); }
if (msg.src === 'surplus')    { surplus = Number(msg.val); flow.set('surplus', surplus); }
if (msg.src === 'venus_grid') { vgrid   = Number(msg.val); flow.set('vgrid', vgrid); }

const now      = Date.now();
const mqttOut  = [];
const fileOut  = [];
const logsBuf  = [];

// Lagerung hat Vorrang: dann NICHTS machen
if (storageActive) {
    flow.set('tStart', null);
    flow.set('tStop',  null);
    node.status({
        fill: 'blue',
        shape: 'dot',
        text: `[PAUSE wg. Lagerung] soc=${soc ?? 'â€”'} | vgrid=${vgrid ?? 'â€”'}`
    });
    return null;
}

// Status anzeigen
node.status({
    fill: enabled ? 'green' : 'grey',
    shape: 'dot',
    text: `[WINTER=${enabled ? 'ON' : 'OFF'}] soc=${soc ?? 'â€”'} | vgrid=${vgrid ?? 'â€”'} | surplus=${surplus ?? 'â€”'}`
});

if (!enabled) {
    flow.set('tStart', null);
    flow.set('tStop',  null);
    return null;
}

// ====== PASSIVE anfordern bei Netzbezug & niedrigem SoC ======
if (typeof vgrid === 'number' && typeof soc === 'number' && soc <= MIN_SOC) {
    if (vgrid >= VGRID_STOP_W) {
        if (!tStop) { tStop = now; flow.set('tStop', tStop); }
        const stopDelay = (soc < FAST_SOC_LIMIT) ? STOP_FAST_MS : STOP_FOR_MS;
        const dt = now - tStop;

        node.status({
            fill: 'yellow',
            shape: 'dot',
            text: `[STOPCHK] ongrid=${Math.round(vgrid)}W SoC=${soc}% ${(dt / 1000).toFixed(1)}/${stopDelay / 1000}s`
        });

        if (dt >= stopDelay) {
            pushLog(mqttOut, fileOut, logsBuf, `WINTER â†’ PASSIVE (ongrid=${Math.round(vgrid)}W, SoC=${soc}%)`);
            mqttOut.push({ topic: 'marstek/venus_e/cmnd/mode', payload: 'Passive' });
            flow.set('mute_until', now + PASSIVE_COOLDOWN_MS);
            flow.set('tStart', null);
            flow.set('tStop',  null);
            node.status({ fill: 'red', shape: 'ring', text: `PASSIVE sent (${Math.round(vgrid)}W)` });
        }
    } else { flow.set('tStop', null); tStop = null; }
} else { flow.set('tStop', null); tStop = null; }

// ====== AUTO anfordern bei stabiler Einspeisung ======
const mayStartAuto = (typeof vgrid !== 'number') ? true : (vgrid <= 0);
if (typeof surplus === 'number' && mayStartAuto) {
    if (now >= muteUntil) {
        if (surplus <= SURPLUS_START_W) {
            if (!tStart) { tStart = now; flow.set('tStart', tStart); }
            const dt = now - tStart;

            node.status({
                fill: 'green',
                shape: 'dot',
                text: `[AUTOCHK] surplus=${Math.round(surplus)}W ${(dt / 1000).toFixed(1)}/${SURPLUS_FOR_MS / 1000}s`
            });

            if (dt >= SURPLUS_FOR_MS) {
                pushLog(mqttOut, fileOut, logsBuf, `WINTER â†’ AUTO (Einspeisung=${Math.round(surplus)}W)`);
                mqttOut.push({ topic: 'marstek/venus_e/cmnd/mode', payload: 'Auto' });
                flow.set('tStop',  null);
                flow.set('tStart', null);
                node.status({ fill: 'green', shape: 'ring', text: 'AUTO sent' });
            }
        } else { flow.set('tStart', null); tStart = null; }
    } else {
        node.status({
            fill: 'grey',
            shape: 'dot',
            text: `[MUTE] AUTO blockiert noch ${((muteUntil - now) / 1000).toFixed(0)}s`
        });
        flow.set('tStart', null);
        tStart = null;
    }
} else { flow.set('tStart', null); tStart = null; }

// ====== History puffern (max 20) & als JSON via MQTT ======
if (logsBuf.length) {
    const stored = flow.get('winter_log') || [];
    for (const line of logsBuf) stored.push(line);
    while (stored.length > 20) stored.shift();
    flow.set('winter_log', stored);

    mqttOut.push({
        topic: LOG_HISTORY_TOPIC,
        payload: JSON.stringify(stored, null, 2)
    });
}

if (mqttOut.length || fileOut.length) return [mqttOut, fileOut];
return null;

// Normalisieren der gewÃ¼nschten Eingabe (ON/AUTO/etc.)
let raw = String(msg.payload ?? "").trim();
const up = raw.toUpperCase();

if (up === "ON" || up === "AUTO") {
    raw = "Auto";
} else if (up === "OFF" || up === "PASSIVE" || up === "STANDBY") {
    raw = "Passive";
}

const mode = raw; // "Auto", "Passive", "Manual", "AI", ...

function build(m) {
    if (m === "Auto") {
        return {
            id: 310,
            method: "ES.SetMode",
            params: {
                id: 0,
                config: {
                    mode: "Auto",
