                }
            }
        };
    }
    if (m === "Manual") {
        return {
            id: 312,
            method: "ES.SetMode",
            params: {
                id: 0,
                config: {
                    mode: "Manual",
                    manual_cfg: {
                        time_num: 1,
                        start_time: "00:00",
                        end_time: "23:59",
                        week_set: 127,
                        power: -500,
                        enable: 1
                    }
                }
            }
        };
    }
    return {
        id: 313,
        method: "ES.SetMode",
        params: {
            id: 0,
            config: {
                mode: "Passive",
                passive_cfg: {
                    power: 0,
                    cd_time: 300
                }
            }
        }
    };
}

const cmd = build(mode);
const out1 = {
    payload: Buffer.from(JSON.stringify(cmd), "utf8")
};

const mqtt = [
    {
        topic: "marstek/venus_e/stat/mode",
        payload: mode
    }
];

setTimeout(() => {
    const getCmd = {
        id: 320,
        method: "ES.GetMode",
        params: { id: 0 }
    };
    node.send([
        [{ payload: Buffer.from(JSON.stringify(getCmd), "utf8") }],
        null
    ]);
}, 400);

node.send([
    [out1],
    null
]);

node.send([
    null,
    mqtt
]);

return null;

const v = Number(msg.payload);
if (Number.isNaN(v)) return null;
msg.src = 'soc';
msg.val = v;
return msg;

const v = Number(msg.payload);
if (Number.isNaN(v)) return null;
msg.src = 'venus_grid';
msg.val = v;
return msg;

// --- Nur diese Keys loggen ---
const ALLOWED = new Set([
  'bat_soc', 'em_ct_state', 'em_total_power', 'bat_temp_c', 'bat_capacity', 'rated_capacity',
  'charg_flag', 'dischrg_flag', 'ongrid_power', 'offgrid_power', 'es_bat_soc', 'fw_ver'
]);

const leaf = String(msg.topic || '').split('/').pop();
if (!ALLOWED.has(leaf)) return null;

// --- Wert umwandeln ---
let val = msg.payload;
if (leaf === 'charg_flag' || leaf === 'dischrg_flag') {
  if (typeof val === 'boolean') val = val ? 1 : 0;
  else {
    const s = String(val).trim().toLowerCase();
    if (['true','on','1'].includes(s)) val = 1;
    else if (['false','off','0'].includes(s)) val = 0;
    else val = Number(s);
  }
} else {
  const n = Number(val);
  if (Number.isFinite(n)) val = n; else {
    const n2 = Number(String(val).replace(/[^-\d.]/g, ''));
    if (!Number.isFinite(n2)) return null;
    val = n2;
  }
}

msg.payload = {};
msg.payload[leaf] = val;
msg.tags = { device_id: 'marstek_venus_e_v3_UDP' };
return msg;

// ===== Statusâ†’Log (to file) v1.0 =====
// Schreibt eine Zeile pro Ereignis in die Logdatei (via File-Node)
// Erwartete Inputs (alle via MQTT-In an diesen Node):
//  - marstek/venus_e/stat/mode           -> tatsÃ¤chlicher GerÃ¤temodus
//  - marstek/venus_e/cmnd/mode           -> manuelle/HA-Befehle
//  - marstek/venus_e/stat/storage_info   -> Storage-Ereignisse
//  - marstek/winter/stat/winter_mode     -> Winterbetrieb ON/OFF

const TOPIC_MODE_STAT   = 'marstek/venus_e/stat/mode';
const TOPIC_MODE_CMND   = 'marstek/venus_e/cmnd/mode';
const TOPIC_STORAGE_INFO= 'marstek/venus_e/stat/storage_info';
const TOPIC_WINTER_STAT = 'marstek/winter/stat/winter_mode';

const now = Date.now();
const tISO = () => new Date().toISOString();

function line(s){ return { payload: `[${tISO()}] ${s}` }; }

const topic = String(msg.topic || '');
const payloadStr = String(msg.payload ?? '').trim();

switch (topic) {
  case TOPIC_MODE_STAT: {
    const newMode = payloadStr || 'â€”';
    const lastMode = flow.get('log_last_mode');
    if (lastMode === undefined) {
      flow.set('log_last_mode', newMode);
      return line(`MODE init: ${newMode}`);
    }
    if (newMode !== lastMode) {
      const lastCmd = flow.get('log_last_cmd') || null;
      let reason = '';
      if (lastCmd && (now - lastCmd.t) <= 5000) reason = ` (cause=cmd:${lastCmd.mode})`;
      flow.set('log_last_mode', newMode);
      return line(`MODE: ${lastMode} -> ${newMode}${reason}`);
    }
    return null;
  }

  case TOPIC_MODE_CMND: {
    flow.set('log_last_cmd', { mode: payloadStr || 'â€”', t: now });
    return line(`CMD: request ${payloadStr || 'â€”'}`);
  }

  case TOPIC_STORAGE_INFO: {
    return line(`STORAGE: ${payloadStr || 'â€”'}`);
  }

  case TOPIC_WINTER_STAT: {
    const newState = (payloadStr.toUpperCase()==='ON') ? 'ON' : 'OFF';
    const lastState = flow.get('log_last_winter');
    if (lastState === undefined) {
      flow.set('log_last_winter', newState);
      return line(`WINTER init: ${newState}`);
    }
    if (newState !== lastState) {
      flow.set('log_last_winter', newState);
      return line(`WINTER: ${lastState} -> ${newState}`);
    }
    return null;
  }

  default:
    return null;
}

