const modeRaw   = String(msg.payload || "").trim();   // z.B. "Manual", "Passive"
const modeLower = modeRaw.toLowerCase();

const now                = Date.now();
let   storageActive      = flow.get("storage_active")      || false;
let   graceUntil         = flow.get("storage_grace_until") || 0;
const graceMode250       = flow.get("storage_grace_mode250") || false;
const targetSoc          = flow.get("storage_target")      || "?";

let udpOutArr  = [];
let mqttOutArr = [];

// 0) Falls komplett inaktiv (keine Lagerung und keine Grace geplant)
//    -> nichts tun.
if (!storageActive && !graceUntil){
    setStatus("grey", "ring", `idle (mode=${modeRaw})`);
    return null;
}

// 1) Sind wir in Grace?
//    inGrace bedeutet: Grace lÃ¤uft noch (now < graceUntil) UND wurde schon gestartet
const inGrace = (graceUntil && now < graceUntil);

// 2) Grace ist vorbei?
const graceFinished = (graceUntil && now >= graceUntil);

// CASE C: Grace fertig -> sauberer Abschluss
// -----------------------------------------
if (graceFinished){
    // final aufrÃ¤umen
    flow.set("storage_grace_until", 0);
    flow.set("storage_grace_mode250", false);
    flow.set("storage_active", false);

    mqttOutArr.push(
        mqttMsg("marstek/venus_e/stat/storage_info",
