    return [
        udpOutArr.length ? udpOutArr : null,
        mqttOutArr.length ? mqttOutArr : null
    ];
}

// CASE B: Wir sind in Grace (Nachlaufphase halten auf Ziel-SoC)
// ------------------------------------------------------------
if (inGrace){
    const secLeft = Math.round((graceUntil - now)/1000);

    if (modeLower === "manual"){
        // Alles gut, GerÃ¤t lÃ¤dt/ hÃ¤lt gerade.
        setStatus("blue","dot",`Grace lÃ¤uft ${secLeft}s (-250W hold)`);

