            mqttMsg("marstek/venus_e/stat/mode","Manual")
        );

        setStatus("blue","ring",`Grace reassert (-250W) ${secLeft}s left`);

        return [
            udpOutArr.length ? udpOutArr : null,
            mqttOutArr.length ? mqttOutArr : null
        ];
    }
