    flow.set('storage_grace_until',   graceUntil);
    flow.set('storage_grace_mode250', graceMode250);

    // WÃ¤hrend der Grace bleibt storage_active TRUE,
    // damit der Wintercontroller drauÃŸen bleibt und wir weiter steuern dÃ¼rfen.

    node.status({
        fill: 'yellow',
        shape: 'dot',
        text: `Nachlauf aktiv @${socNow}% (bis ${new Date(graceUntil).toLocaleTimeString()})`
    });

    // Sofort umschalten auf Manual mit HOLD_POWER_W (-250W)
    const cmdManualHold = buildManual(HOLD_POWER_W);
    udpMsgs.push({
        payload: Buffer.from(JSON.stringify(cmdManualHold),'utf8')
    });

    mqttMsgs.push({
        topic: 'marstek/venus_e/stat/storage_info',
        payload: `storage:grace-start soc=${socNow}% target=${target}% holdUntil=${graceUntil} holdPower=${HOLD_POWER_W}W`
    });

    // Optimistischer Mode -> "Manual"
    mqttMsgs.push({
        topic: 'marstek/venus_e/stat/mode',
