    return [udpMsgs, mqttMsgs];
}

// === FALL C: Grace ist vorbei ODER SoC ist eh schon >= target+1 ===
if (graceExpired || socHighEnough) {

    // Jetzt wirklich final abschalten:
    flow.set('storage_active', false);
    flow.set('storage_grace_until', 0);
