        shape: 'dot',
        text: `Ziel erreicht (${socNow}%) â†’ Passive`
    });

    // UDP: auf Passive umschalten
    const cmdPassive = buildPassiveCmd();
    udpMsgs.push({
        payload: Buffer.from(JSON.stringify(cmdPassive),'utf8')
    });

    // MQTT: melden dass wir fertig sind
    mqttMsgs.push(
        {
            topic: 'marstek/venus_e/stat/storage_info',
            payload: `storage:done soc=${socNow}% -> Passive (grace end)`
        },
        {
            topic: 'marstek/venus_e/stat/mode',
            payload: FINAL_MODE
        },
        {
            topic: 'marstek/storage/stat/enable',
            payload: 'OFF'
        }
    );

    return [udpMsgs, mqttMsgs];
}

